case class UURow(URL1 : String,URL2:String)
def httpNom(line : String) : String = {
try {
  val caseInsensitivePattern = """^(?i)(http|ftp|https)://""".r
  return  caseInsensitivePattern.replaceFirstIn(line.trim(),"http://")
  //      line replaceAll (caseInsensitivePattern, )
}
catch {
  case ex: Exception => ""
}
}

def hash(s:String)={
    val m = java.security.MessageDigest.getInstance("MD5")
    val b = s.getBytes("UTF-8")
    m.update(b,0,b.length)
    new java.math.BigInteger(1,m.digest()).toString(16).trim()
 }

def getHost(line : String) : String = {
    if (line == null || line.trim().equals("")) {
      return "";
    }
    try {
      val caseInsensitivePattern = """(?<=//|)((\w)+\.)+\w+(:\d*)?""".r
      return  caseInsensitivePattern.findFirstIn(line).get
      //      line replaceAll (caseInsensitivePattern, )
    }
    catch {
      case ex: Exception => ""
    }
  }

  def lineToM(l : String)= {
    var line = l
    if(line.startsWith("\"")&&line.endsWith("\"")){
      line = line.substring(1,line.length-1)
    }
    if(line.startsWith("[")&&line.endsWith("]")){
      line = line.substring(1,line.length-1)
    }
    val regex = "@[A-Za-z._]{1,30}:".r
    val tags = regex findAllIn line toArray
    val contents = line.split("@[A-Za-z._]{1,30}:",-1)

    var M:Map[String,String] = Map()

    for(i<-0 until tags.length){
      var tag = tags(i)
      var content = contents(i+1)
      M += (tag -> content.trim())
    }
    M
  }

