def DateFormat(time:String):String={
    var sdf:SimpleDateFormat = new SimpleDateFormat("yyyyMMdd")
    var date:String = sdf.format(new Date((time.toLong*1000)))
    return date
}

def decode(value:String): String = URLDecoder.decode(value, "gbk")

def getRow(line : String):Option[Logrow]= {
    //    var userid, uuid, page, time, search_type = ""
    val regex="""^\d+$""".r
    var userid, uuid, page,time,search_type,unknown = ""
    var tmp = line.trim().split('\t')
    if (tmp.length < 2) {
      return None
    }
    var tmp0 = tmp(0).trim().split('#')
    if (tmp0.length != 5 && tmp0.length != 6) {
      return None
    }
    if(tmp0.length == 5){
      userid = tmp0(0)
      uuid = tmp0(1)
      page = tmp0(2)
      time = tmp0(3)
      search_type = tmp0(4)
    }else if(tmp0.length == 6){
      userid = tmp0(0)
      uuid = tmp0(1)
      page = tmp0(2)
      time = tmp0(3)
      search_type = tmp0(4)
      unknown = tmp0(5)
    }
    var query = decode(tmp(1))
    var urls:List[Map[String,String]] = List()
    var urlblock:Map[String,String]= Map()
    var cnt = 0
    for(i <- 2 to tmp.length-1){
      //      println(tmp(i))
      //      println(tmp(i).trim().split("#",-1).length)
      if (((tmp(i).trim().split("#",-1).length) >= 4) && regex.findFirstMatchIn((tmp(i).trim().split("#",-1)(1))) != None){
        var tmp_i = tmp(i).trim().split("#",-1)
        if(tmp_i.length != 4){
          tmp_i = Array("","","","")
        }
        var Array(vrid, ph_3_1, ph_3_2, baseurl) = tmp_i
        baseurl = decode(baseurl)
        urlblock += ("vrid" -> vrid)
        urlblock += ("3_1" -> ph_3_1)
        urlblock += ("3_2" -> ph_3_2)
        urlblock += ("baseurl" -> baseurl)
        urls = urls :+ urlblock
        urlblock = Map()
        cnt = 0
      }else{
        if(cnt==0){
          urlblock += ("wapurl" -> tmp(i))
          cnt = cnt+1
        }else if(cnt==1){
          urlblock += ("clk" -> tmp(i))
          cnt = cnt+1
        }else if(cnt==2){
          urlblock += ("2" -> tmp(i))
          cnt = cnt+1
        }
      }
    }
    return Some(new Logrow(userid, uuid, page, time, search_type, query, urls))
  }

 def trans_dataframe(row:Logrow)= {
    // val filterURI = List("ask.yaolan.com", "www.120ask.com", "www.babytree.com", "club.xywy.com", "ask.39.net", 
    //   "www.haodf.com", "ask.ci123.com", "ask.familydoctor.com.cn", "bbs.tianya.cn")
    val filterURI = List("www.120ask.com", "wzjlsogoupc.guahao.com", "www.mfk.com", "sns.guahao.com", "www.cnkang.com", 
      "www.youlai.cn", "www.39yst.com", "www.sytown.cn", "ask.39.net")
    var list = ArrayBuffer[(String,String)]()
    var urls = row.urls.map(urlblock => {
      var url = ""
      if (urlblock.get("baseurl").getOrElse("") == "") {
        url = urlblock.get("wapurl").getOrElse("").toString
      } else {
        url = urlblock.get("baseurl").getOrElse("").toString
      }
      url = httpNom(url)
      url
    })
    urls = urls.filter(x=>filterURI.contains(getHost(x))).sorted
    // urls.foreach(println)
    // print(urls.length)
    val len = urls.length
    if(len>1){
      var url_1 = ""
      var url_2 = ""

      for (i <- 0 to len - 2) {
        url_1  = urls(i)
        for (j <- i+1 to len - 1) {
          url_2  = urls(j)
          list += ((url_1,url_2))
        }
      }
    }
    list.toList
  }



