def lineToQAU(line : String)= {

    val regex = "@[A-Za-z._]{1,30}:".r
    val tags = regex findAllIn line toArray
    val contents = line.split("@[A-Za-z._]{1,30}:",-1)
   
  
    var ans = ""
    var url = ""
    var ask=""
    try{
      for(i<-0 until tags.length){
        var tag = tags(i)
        var content = contents(i+1)
     
        if(tag=="@ANS.CONTENT:"){
          ans = content.trim()
        }else if(tag=="@ASK.TITLE:"){
          ask = content.trim()
        }else if(tag=="@URI:"){
          url = content.trim()
          url = httpNom(url)
        }

        //     M += (tag -> content)
      }
    }catch{
      case e: Exception => {
        ask = ""
        ans = ""
        url = ""
      }
    }
    (ask,ans,url)
  }

